mapper.select:
SELECT ini.nombre AS iniciativaNombre, ini.descripcion AS iniciativaDescripcion, ini.fechapropuesta AS iniciativaFechaPropuesta, ini.fechacierre AS iniciativaFechaCierre, ini.estado AS iniciativaEstado,
usr.correo AS usuarioCorreo, usr.nombre AS usuarioNombre, usr.tipo AS usuarioTipo, usr.area AS usuarioArea,
areas.id AS areaId, areas.nombre AS areaNombre, areas.descripcion AS areaDescripcion FROM iniciativas ini, usuarios usr, areas WHERE ini.proponente = usr.correo AND usr.area = areas.id AND ini.nombre = #{nombreIni};

SELECT ini.nombre AS iniciativaNombre, ini.descripcion AS iniciativaDescripcion, ini.fechapropuesta AS iniciativaFechaPropuesta, ini.fechacierre AS iniciativaFechaCierre, ini.estado AS iniciativaEstado,
usr.correo AS usuarioCorreo, usr.nombre AS usuarioNombre, usr.tipo AS usuarioTipo, usr.area AS usuarioArea, pclaves.descripcion AS iniciativaPalabrasClave,
areas.id AS areaId, areas.nombre AS areaNombre, areas.descripcion AS areaDescripcion FROM iniciativas ini, usuarios usr, areas, palabrasclave pclaves WHERE ini.proponente = usr.correo AND usr.area = areas.id AND pclaves.iniciativa=ini.nombre AND pclaves.descripcion = #{clave};
